mapper.select:
select status
from room
where roomID = #{roomId}
  and hostelID = #{hostelId}

mapper.update:
update room
set status        = #{status},
    administrator = #{administrator}
where roomID = #{roomId}
  and hostelID = #{hostelId}

mapper.select:
select count(*)
from room
where hostelID = #{hostelId};

select *
from room
where hostelID = #{hostelId}
limit #{pageNo},#{pageSize}

select *
from room
where hostelID = #{hostelId}
  and status != 1
  and type = #{type}
